import React from "react";
import RandomForm from "./randomForm";
import { useGlobalContext } from '../context/context';
import { useEffect } from "react";
import axios from 'axios'

const FavouriteContent = () => {

    const {
        submitRandom,
        setSubmitRandom,
        setDataActivity,
    dataActivity } = useGlobalContext();

    const handleSubmitRandom = e => {
        e.preventDefault();
        setSubmitRandom(!submitRandom);
    }
    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity/`,
        })
            .then(response => {
                setDataActivity(response.data.activity); console.log(response.data)
            });
    }, [submitRandom])

    if (dataActivity) {
        return (
            <div id='main-content' className="container">
                    <RandomForm />
                <h1>{dataActivity}</h1>
            </div>
        );
    }
};

export default FavouriteContent;
