import React from "react";
import RandomForm from "./randomForm";
import { useGlobalContext } from '../context/context';
import { useEffect, useState } from "react";
import axios from 'axios'
import FavouriteList from "./FavouriteList";
import Favourites from "../storage/favourites";

const FavouriteContent = () => {

    const {
        submitRandom,
        setSubmitRandom,
        setDataActivity,
        dataActivity } = useGlobalContext();

    const [favourite, setFavourite] = useState(Favourites);

    const removeItem = (id) => {
        setFavourite((oldValue) => oldValue.filter((value) => value.id !== id));
    };

    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity/`,
        })
            .then(response => {
                setDataActivity(response.data.activity);
            });
    }, [submitRandom])

    if (dataActivity) {
        return (
            <div id='main-content' className="container">
                <div className="row py-5">
                    <div className="col-12 col-md-6">
                        <h1>Favorites activities</h1>
                        <div className="people-list">
                            <FavouriteList data={favourite} removeItem={removeItem} />
                        </div>
                        <div className="btn-group">
                            <button className="btn btn-reset" onClick={reloadAllItem}>
                                {" "}
                                Reload default task{" "}
                            </button>
                            <button className="btn btn-delete" onClick={() => setFavourite([])}>
                                {" "}
                                All done
                            </button>
                        </div>

                    </div>
                    <RandomForm />
                </div>
                <h1>{dataActivity}</h1>
            </div>
        );
    }
};

export default FavouriteContent;
