import React from "react";
import axios from "axios";
import { useEffect } from "react";
import Form from "../components/CustomSearchComponent";
import { useGlobalContext } from "../context/context";
import AddFavourite from "../components/AddFavouriteButton";
import RandomComponent from "../components/RandomComponent";

const SearchContent = () => {
    const { typeOption,
        partecipantInput,
        submit,
        submitRandom,
        setDataActivity,
        dataActivity,
        setErrorResponse,
        errorResponse,
    } = useGlobalContext();

    useEffect(() => {
        axios({
            method: 'get',
            url: `https://www.boredapi.com/api/activity?participants=${partecipantInput}&type=${typeOption}`,
        })
            .then(response => {
                setDataActivity(response.data);
            })
            .catch(error => {
                setErrorResponse(error);
            });
    }, [submit]);

    useEffect(() => {
        axios({
            method: 'get',
            url: `https://www.boredapi.com/api/activity/`,
        })
            .then(response => {
                setDataActivity(response.data);
            })
            .catch(error => {
                setErrorResponse(error);
            });
    }, [submitRandom])

    if (dataActivity.activity) {
        return (
            <div id='main-content' className="container">
                <div className="row py-3">
                    <Form />
                    <div className="col-12 col-md-6 py-3 align-self-center">
                        <h2>{dataActivity.activity}</h2>
                        <AddFavourite />
                    </div>
                </div>
            </div >
        );
    }
    if (!dataActivity.activity) {
        if (errorResponse) {
            return (
                <div id='main-content' className="container">
                    <div className="row py-3">
                        <Form />
                    </div>
                    <h1>Server non raggiungibile</h1>
                </div>
            )
        }
        else {
            return (
                <div id='main-content' className="container">
                    <div className="row py-3">
                        <Form />
                        <RandomComponent />
                    </div>
                    <h1>Attivit√† non trovata</h1>
                </div>
            )
        }
    }
};

export default SearchContent;
