import React, { useState } from "react";
import Select from 'react-select'


const Form = () => {
    const [typeOption, setTypeOption] = useState('');
    const [descriptionInput, setDescriptionInput] = useState('');

    const options = [
        { value: 'education', label: 'Education' },
        { value: 'recreational', label: 'Recreational' },
        { value: 'social', label: 'Social' },
        { value: 'diy', label: 'Diy' },
        { value: 'charity', label: 'Charity' },
        { value: 'cooking', label: 'Cooking' },
        { value: 'relaxation', label: 'Relaxation' },
        { value: 'music', label: 'Music' },
        { value: 'busywork', label: 'Busywork' },
    ];


    const handleChangeDescription = e => {
        setDescriptionInput(e.target.value);
        console.log(descriptionInput);
    };

    const handleChangeOption = e => {
        e.preventDefault();
        setTypeOption(e.state.value);
        console.log(typeOption);
    };

    const handleSubmit = e => {
        e.preventDefault();
    }


    return (
        <>
            <form className="form-task" onSubmit={handleSubmit}>
                <div className="form-field">
                    <Select options={options} onChange={(e) =>handleChangeOption(e)} />
                </div>
                <div className="form-field">
                    <h4><label htmlFor="numberOfPartecipants">Numero di partecipanti</label></h4>
                    <input type="number" min="1" placehonder="Insert number of partecipants" name="numberOfPartecipants" id="numberOfPartecipants" onChange={handleChangeDescription} value={descriptionInput} />
                </div>
                <div className="form-field">
                    <button className="btn">{"Find activity"}</button>
                </div>
            </form>
        </>
    );
};

export default Form;
