import { useContext, useState, createContext } from "react";
import Favourites from "../storage/favourites";



const AppContext = createContext();

const AppProvider = ({ children }) => {
    const [typeOption, setTypeOption] = useState('education');
    const [partecipantInput, setPartecipantInput] = useState(1);
    const [submit, setSubmit] = useState(true);
    const [submitRandom, setSubmitRandom] = useState(true);
    const [dataActivity, setDataActivity] = useState([]);
    const [favouriteCounter, setFavouriteCounter] = useState(3);

    return <AppContext.Provider value={
        {
            typeOption,
            setTypeOption,
            partecipantInput,
            setPartecipantInput,
            submit,
            setSubmit,
            submitRandom,
            setSubmitRandom,
            dataActivity,
            setDataActivity,
            favouriteCounter,
        }}>
        {children}
    </AppContext.Provider>
}

const useGlobalContext = () => {
    return useContext(AppContext)
}

export { AppProvider, useGlobalContext };
