import React from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from 'react';
import Form from "./form";
import { useGlobalContext } from "../context/context";

const SearchContent = () => {

    const { typeOption,
        partecipantInput,} = useGlobalContext();

    const [dataActivity, setDataActivity] = useState([]);
    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity?type=${typeOption}&?partecipants=${partecipantInput}`,
        })
            .then(response => { setDataActivity(response.data.activity);
            });
    }, [typeOption, partecipantInput])

    return (
        <div id='main-content' className="container">
            <Form/>
            <h1>{dataActivity}</h1>
        </div>
    );
};

export default SearchContent;
