import React from "react";
import RandomForm from "./randomForm";
import { useGlobalContext } from '../context/context';
import { useEffect, useState } from "react";
import axios from 'axios'
import FavouriteList from "./FavouriteList";
import { AiFillHeart } from "react-icons/ai";

const FavouriteContent = () => {

    const {
        submitRandom,
        setDataActivity,
        dataActivity,
        favourite,
        setFavourite } = useGlobalContext();


    const removeItem = (id) => {
        setFavourite(favourite.filter((el) => el.id != id));
    };

    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity/`,
        })
            .then(response => {
                setDataActivity(response.data);
            });
    }, [submitRandom])

    if (dataActivity) {
        return (
            <div id='main-content' className="container">
                <div className="row py-5">
                    <div className="col-12 col-md-6 py-3">
                        <h1>Favorites activities</h1>
                        <div className="people-list">
                            <FavouriteList data={favourite} removeItem={removeItem} />
                        </div>
                        <div className="btn-group">
                            <button className="btn btn-delete" onClick={() => setFavourite([{}])}>
                                {" "}
                                Remove All
                            </button>
                        </div>

                    </div>
                    <RandomForm />
                </div>
                <h2>{dataActivity.activity}</h2> <button className="btn btn-outline-danger" onClick={() => { setFavourite([...favourite, { id: dataActivity.key, title: `${dataActivity.activity}` }]); }}> <AiFillHeart/> Add Favourite</button>
            </div>
        );
    }
};

export default FavouriteContent;
