import React, { useState } from "react";
import Select from 'react-select'


const Form = () => {
    const [typeOption, setTypeOption] = useState('');
    const [partecipantInput, setPartecipantInput] = useState('');

    const options = [
        { value: 'education', label: 'Education' },
        { value: 'recreational', label: 'Recreational' },
        { value: 'social', label: 'Social' },
        { value: 'diy', label: 'Diy' },
        { value: 'charity', label: 'Charity' },
        { value: 'cooking', label: 'Cooking' },
        { value: 'relaxation', label: 'Relaxation' },
        { value: 'music', label: 'Music' },
        { value: 'busywork', label: 'Busywork' },
    ];

    const handleSubmit = e => {
        e.preventDefault();
        console.log(typeOption);
        console.log(partecipantInput);
    }


    return (
        <>
            <form className="form-task" onSubmit={handleSubmit}>
                <h4><label htmlFor="typeOfActivities">Tipo di attivit√†</label></h4>
                <div className="form-field">
                    <Select className='input-query' options={options} onChange={(e) => setTypeOption(e.value)} />
                </div>
                <h4><label htmlFor="numberOfPartecipants">Numero di partecipanti</label></h4>
                <div className="form-field">
                    <input className='input-query py-2 text-center' type="number" min="1" placehonder="Partecipants" name="numberOfPartecipants" id="numberOfPartecipants" onChange={(e) => setPartecipantInput(e.target.value)} />
                </div>
                <div className="form-field">
                    <button className="btn">{"Find activity"}</button>
                </div>
            </form>
        </>
    );
};

export default Form;
