import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import Form from "./form";
import { useGlobalContext } from "../context/context";
import { AiFillHeart } from "react-icons/ai";
const SearchContent = () => {

    const { typeOption,
        partecipantInput,
        submit,
        submitRandom,
        favourite,
        setFavourite } = useGlobalContext();

    const [dataActivity, setDataActivity] = useState([]);

    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity?participants=${partecipantInput}&type=${typeOption}`,
        })
            .then(response => {
                setDataActivity(response.data);
            });
    }, [submit]);

    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity/`,
        })
            .then(response => {
                setDataActivity(response.data);
            });
    }, [submitRandom])

    if (dataActivity.activity) {
        return (
            <div id='main-content' className="container">
                <Form />
                <h2>{dataActivity.activity}</h2> <button className="btn btn-outline-danger" onClick={() => { setFavourite([...favourite, { id: dataActivity.key, title: `${dataActivity.activity}` }]); }}> <AiFillHeart/> Add Favourite</button>
            </div >
        );
