import React, { useState } from "react";
import Select from 'react-select';
import { useGlobalContext } from "../context/context";


const Form = () => {

    const { typeOption,
        setTypeOption,
        partecipantInput,
        setPartecipantInput, } = useGlobalContext();

    const options = [
        { value: 'education', label: 'Education' },
        { value: 'recreational', label: 'Recreational' },
        { value: 'social', label: 'Social' },
        { value: 'diy', label: 'Diy' },
        { value: 'charity', label: 'Charity' },
        { value: 'cooking', label: 'Cooking' },
        { value: 'relaxation', label: 'Relaxation' },
        { value: 'music', label: 'Music' },
        { value: 'busywork', label: 'Busywork' },
    ];

    const handleSubmit = e => {
        e.preventDefault();
        console.log(typeOption);
        console.log(partecipantInput);
    }


    return (
        <>
            <div className="h-25"></div>
            <form className="form-task" onSubmit={handleSubmit}>
                <h4><label htmlFor="typeOfActivities">Tipo di attivit√†</label></h4>
                <div className="select-field">
                    <Select defaultValue={options[0]} className='input-query-select' options={options} onChange={(e) => setTypeOption(e.value)} />
                </div>
                <br />
                <h4><label htmlFor="numberOfPartecipants">Minimo di partecipanti</label></h4>
                <div className="form-field">
                    <button className="btn bg-light  button-activity" onClick={() => setPartecipantInput(partecipantInput - 1)}>-</button>
                    <input className='input-query-partecipants py-1 text-center' type="number" placehonder="Partecipants" name="numberOfPartecipants" id="numberOfPartecipants" onChange={(e) => setPartecipantInput(e.target.value)} />
                    <button className="btn bg-light  button-activity" onClick={() => setPartecipantInput(partecipantInput + 1)}>+</button>
                </div>
                <br />
                <div className="form-field">
                    <button className="btn bg-light">Find activity</button>
                </div>
                <br />
            </form>
        </>
    );
};

export default Form;
