import React from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from 'react';
import Form from "./form";
import { useGlobalContext } from "../context/context";

const SearchContent = () => {

    const { typeOption,
        partecipantInput,
        submit, } = useGlobalContext();

    const [dataActivity, setDataActivity] = useState([]);
    useEffect(() => {
        axios({
            method: 'get',
            url: `http://www.boredapi.com/api/activity?participants=${partecipantInput}&type=${typeOption}`,
        })
            .then(response => {
                setDataActivity(response.data.activity); console.log(response.data)
            });
    }, [submit])

    if (dataActivity) {
        return (
            <div id='main-content' className="container">
                <Form />
                <h1>{dataActivity}</h1>
            </div>
        );
    }
    if (!dataActivity) {
        return (
            <div id='main-content' className="container">
                <Form />
                <h1>`There is no activity with type ${typeOption} and number of partecipants equal to'${partecipantInput}</h1>
            </div>
        )
    }
};

export default SearchContent;
